import React, { useState } from "react";
import { Card, Button, Form, InputGroup, FormControl } from "react-bootstrap";

const ProductList = ({ products }) => {
  const [selectedProduct, setSelectedProduct] = useState({});

  const handleEdit = (product) => {
    setSelectedProduct(product);
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    const updatedProduct = {
      ...selectedProduct,
      countInStock: Number(e.target.countInStock.value),
    };
    console.log(updatedProduct);
    // perform the update product action
  };

  const minimumStockProducts = products.filter(
    (product) => product.countInStock < 10
  );

  return (
    <div>
      <h3 className="my-3">Minimum Stocks</h3>
      {minimumStockProducts.map((product) => (
        <Card className="my-3 p-3 rounded" key={product._id}>
          <Card.Body>
            <Card.Title>{product.name}</Card.Title>
            <Card.Img src={product.image} alt={product.name} />
            <Card.Text>Price: ${product.price}</Card.Text>
            <Card.Text>Count in Stock: {product.countInStock}</Card.Text>
            <Button onClick={() => handleEdit(product)}>Edit</Button>
          </Card.Body>
        </Card>
      ))}
      {selectedProduct._id && (
        <Form onSubmit={handleUpdate}>
          <InputGroup className="my-3">
            <InputGroup.Prepend>
              <InputGroup.Text>Count in Stock</InputGroup.Text>
            </InputGroup.Prepend>
            <FormControl
              type="number"
              name="countInStock"
              defaultValue={selectedProduct.countInStock}
            />
            <InputGroup.Append>
              <Button type="submit">Update</Button>
            </InputGroup.Append>
          </InputGroup>
        </Form>
      )}
    </div>
  );
};

export default ProductList;
