import React, { useState } from "react";
import { Card, Row, Col } from "react-bootstrap";
import { LinkContainer } from 'react-router-bootstrap'
// import { Table, Button, Row, Col,Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Loader from '../components/Loader'
import Message from '../components/Message'
import Paginate from '../components/Paginate'
import { listProducts, deleteProduct, createProduct } from '../actions/productActions'
import { PRODUCT_CREATE_RESET } from '../constants/productConstants'

function ProductListScreen({ products }) {
    const [selectedCategory, setSelectedCategory] = useState("All");
    const categories = ["All", ...new Set(products.map(product => product.category))];

    const filteredProducts =
      selectedCategory === "All"
        ? products
        : products.filter(product => product.category === selectedCategory);

    return (
      <div>
        <h4>Minimum Stock</h4>
        <div className="my-2">
          <label htmlFor="category">Category:</label>
          <select
            id="category"
            value={selectedCategory}
            onChange={e => setSelectedCategory(e.target.value)}
          >
            {categories.map(category => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>
        <Row className="my-1">
          {Object.entries(
            filteredProducts
              .filter(product => product.countInStock < 10)
              .reduce((result, product) => {
                if (!result[product.category]) {
                  result[product.category] = [];
                }
                result[product.category].push(product);
                return result;
              }, {})
          )
            .filter(([category, products]) => products.length > 0)
            .map(([category, products]) => (
              <Col xs={12} key={category}>
                <h5>{category}</h5>
                <Row>
                  {products.map(product => (
                    <Col xs={12} md={4} key={product._id} className="my-3">
                      <Card>
                        <Card.Img variant="top" src={product.image} />
                        <Card.Body>
                          <Card.Title>{product.name}</Card.Title>
                          <Card.Text>
                            Price: ${product.price}
                          </Card.Text>
                          <Card.Text>
                            Count in Stock:{" "}
                            <span style={{ color: "red", fontWeight: "bold" }}>
                              {product.countInStock}
                            </span>
                          </Card.Text>
                        </Card.Body>
                      </Card>
                    </Col>
                  ))}
                </Row>
              </Col>
            ))}
        </Row>
      </div>
    );
  }

  export default ProductListScreen;
