import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Navbar, Nav, Container, Row, NavDropdown, Form, Button } from 'react-bootstrap'
import { LinkContainer } from 'react-router-bootstrap'
import SearchBox from './SearchBox'
import { logout } from '../actions/userActions'
import { listCategories } from '../actions/categoryActions'

function Header() {

    const userLogin = useSelector(state => state.userLogin)
    const { userInfo } = userLogin

    const categoryList = useSelector(state => state.categoryList)
    const { categories } = categoryList

    const dispatch = useDispatch()

    const logoutHandler = () => {
        dispatch(logout())
    }

    const [selectedCategory, setSelectedCategory] = useState('')

    const categorySelectHandler = (e) => {
        setSelectedCategory(e.target.value)
    }

    const submitHandler = (e) => {
        e.preventDefault()
        if (selectedCategory) {
            window.location = `/search/category/${selectedCategory}`
        } else {
            window.location = '/'
        }
    }

    useEffect(() => {
        dispatch(listCategories())
    }, [dispatch])

    return (
        <header>
            <Navbar bg="primary" variant="dark" expand="lg" collapseOnSelect>
                <Container>
                    <LinkContainer to='/'>
                        <Navbar.Brand>BikieMart</Navbar.Brand>
                    </LinkContainer>

                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Form onSubmit={submitHandler} inline className='mr-auto'>
                            <Form.Control as="select" value={selectedCategory} onChange={categorySelectHandler}>
                                <option value="">All Categories</option>
                                {categories && categories.map(category => (
                                    <option key={category._id} value={category._id}>{category.name}</option>
                                ))}
                            </Form.Control>
                            <Button type='submit' variant='outline-light' className='p-2 ml-2'>
                                Filter
                            </Button>
                        </Form>
                        <SearchBox />

                        <Nav className="ml-auto">

                            <LinkContainer to='/cart'>
                                <Nav.Link ><i className="fas fa-shopping-cart"></i>Cart</Nav.Link>
                            </LinkContainer>

                            {userInfo ? (
                                <NavDropdown title={userInfo.name} id='username'>

                                    <NavDropdown.Item onClick={logoutHandler}>Logout</NavDropdown.Item>

                                </NavDropdown>
                            ) : (
                                    <LinkContainer to='/login'>
                                        <Nav.Link><i className="fas fa-user"></i>Login</Nav.Link>
                                    </LinkContainer>
                                )}


                            {userInfo && userInfo.isAdmin && (
                                <NavDropdown title='Admin' id='adminmenue'>

                                    <LinkContainer to='/admin/productlist'>
                                        <NavDropdown.Item>Stocks</NavDropdown.Item>
                                    </LinkContainer>

                                </NavDropdown>
                            )}


                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </header>
    )
}

export default Header
