import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Navbar, Nav, Container, Row, NavDropdown, Form, Button } from 'react-bootstrap'
import { LinkContainer } from 'react-router-bootstrap'
import { logout } from '../actions/userActions'
import { listCategories } from '../actions/categoryActions'

function Header() {

    const [keyword, setKeyword] = useState('')

    const dispatch = useDispatch()

    const userLogin = useSelector(state => state.userLogin)
    const { userInfo } = userLogin

    const categoryList = useSelector(state => state.categoryList)
    const { loading, error, categories } = categoryList

    React.useEffect(() => {
        dispatch(listCategories())
    }, [dispatch])

    const logoutHandler = () => {
        dispatch(logout())
    }

    const submitHandler = (e) => {
        e.preventDefault()
        if (keyword.trim()) {
            window.location.href = `/search/${keyword}`
        } else {
            window.location.href = '/'
        }
    }

    return (
        <header>
            <Navbar bg="primary" variant="dark" expand="lg" collapseOnSelect>
                <Container>
                    <LinkContainer to='/'>
                        <Navbar.Brand>BikieMart</Navbar.Brand>
                    </LinkContainer>

                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Form onSubmit={submitHandler} inline>
                            <Form.Control
                                type='text'
                                name='q'
                                onChange={(e) => setKeyword(e.target.value)}
                                placeholder='Search Products...'
                                className='mr-sm-2 ml-sm-5'
                            ></Form.Control>
                            <Button type='submit' variant='outline-success' className='p-2'>
                                Search
                            </Button>
                        </Form>
                        <Nav className="ml-auto">
                            <NavDropdown title='Categories' id='categories'>
                                {loading ? (
                                    <NavDropdown.Item>Loading...</NavDropdown.Item>
                                ) : error ? (
                                    <NavDropdown.Item>{error}</NavDropdown.Item>
                                ) : (
                                    categories.map((category) => (
                                        <LinkContainer key={category} to={`/category/${category}`}>
                                            <NavDropdown.Item>{category}</NavDropdown.Item>
                                        </LinkContainer>
                                    ))
                                )}
                            </NavDropdown>

                            <LinkContainer to='/cart'>
                                <Nav.Link ><i className="fas fa-shopping-cart"></i>Cart</Nav.Link>
                            </LinkContainer>

                            {userInfo ? (
                                <NavDropdown title={userInfo.name} id='username'>
                                    <NavDropdown.Item onClick={logoutHandler}>Logout</NavDropdown.Item>
                                </NavDropdown>
                            ) : (
                                <LinkContainer to='/login'>
                                    <Nav.Link><i className="fas fa-user"></i>Login</Nav.Link>
                                </LinkContainer>
                            )}

                            {userInfo && userInfo.isAdmin && (
                                <NavDropdown title='Admin' id='adminmenue'>
                                    <LinkContainer to='/admin/productlist'>
                                        <NavDropdown.Item>Stocks</NavDropdown.Item>
                                    </LinkContainer>
                                </NavDropdown>
                            )}
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </header>
    )
}

export default Header;
